<?phpheader("Content-Type=text/html; charset=UTF-8");require_once "include.php";if(!connectSQLSever()) {    returnData("", mysqli_connect_error());}$link = connectDB();if(!$link) {    returnData("", mysqli_connect_error());}$username = $_POST["username"];$verify = $_POST['verify'];$sql = "select * from User where username = '{$username}';";$row = fetchData($sql, $link);if($row['verify'] != $verify) {    returnData("", "请检查验证信息", $link);}if($row) {    $data = array("username" => $username, "password" => $row["password"]);    returnData($data, "", $link, "true");    } else {    returnData("", "请检查用户名和验证信息", $link);}