<?phpheader("Content-Type=text/html; charset=UTF-8");require_once "include.php";if(!connectSQLSever()) {    returnData("", mysqli_connect_error());}$link = connectDB();if(!$link) {    returnData("", mysqli_connect_error());}$username = $_POST["username"];$sql = "select * from User where username = '{$username}' limit 1;";$row = fetchData($sql, $link);if($row) {    $data = array("username" => $username, "password" => $row["password"]);    returnData($data, "", $link, "true");    } else {    returnData("", "查无此人", $link);}