<?phpheader("content-type=text/html; charset=utf-8");require_once 'include.php';if(!connectSQLSever()) {    returnData("", mysqli_connect_error());}$link = connectDB();if(!$link) {    returnData("", mysqli_connect_error());}$username = $_POST["username"];$friends = array();$sql = "select * from Friend where username = '{$username}' order by friend";$row = fetchMultiData($sql, $link);//var_dump($row);$count = count($row);// var_dump($count);if($count) {    for($i = 0; $i < $count; $i++) {        $friendName = $row[$i][2];        $sqlSelect = "select * from User where username = '{$friendName}'";        $result = fetchData($sqlSelect, $link);        $friends[$i] = $result;        //        $friends[$i] = $row[$i][2];    }        //    returnData($friends, "", $link,"true");} else {    $friends = array();    //    returnData("", "没有关注其他小伙伴",$link);    }$fans = array();$sql = "select * from Friend where friend = '{$username}' ORDER BY username;";$row = fetchMultiData($sql, $link);$count = count($row);if($count) {    for($i = 0; $i < $count; $i++) {        $fansName = $row[$i][1];        $sqlSelect = "select * from User where username = '{$fansName}';";        $result = fetchData($sqlSelect, $link);        $fans[$i] = $result;    }} else {    $fans = array();}$data = array("friends" => $friends, "fans" => $fans);returnData($data, "", $link, "true");